const initialState = {
    videos: []
  };
  // action -> {type: "ABC", payload: 2}
  const videosReducer = (state = initialState, { type, payload }) => {
    switch (type) {
      // the payload is an object
      case "SET_VIDEOS":
        return {
            videos: payload,
        };
        case "WATCH_LATER":
            return {
                videos:[] ,
            };

      default:
        return state;
    }
  };

  export default videosReducer;
